BRL_CHEF_ADDR = "0x35CC71888DBb9FfB777337324a4A60fdBAA19DDE"

tr = Tracker(["weth","near","brl"])

def get_APR():
    w3 = Web3(Web3.HTTPProvider(WEB3_PROVIDER_URL))
    contract = w3.eth.contract(address=BRL_CHEF_ADDR, abi=CONTRACT_ABI)
    
    pool_info = contract.functions.poolInfo(1).call()
    pair_addr = pool_info[0]
    alloc_point = pool_info[1]

    pair_contract = w3.eth.contract(
        address=pair_addr, 
        abi=UNISWAP_V2_ABI
    )
    token0_contract = w3.eth.contract(
        address=pair_contract.functions.token0().call(), 
        abi=ERC20_ABI
    )
    reserves = pair_contract.functions.getReserves().call()
    
    token0_amount = reserves[0] / 10 ** token0_contract.functions.decimals().call()
    token0_symbol = token0_contract.functions.symbol().call().lower()
    
    current_number = w3.eth.block_number
    multiplier = contract.functions.getMultiplier(
        current_number, 
        current_number + 1
    ).call()

    blocks_per_year = int( (60 / 1.2)*60 * 24 * 365 )
    rewards_per_block = contract.functions.BRLPerBlock().call() / 1e18 * multiplier
    annual_token_emission = rewards_per_block * blocks_per_year
    total_alloc_point = contract.functions.totalAllocPoint().call()
    pool_weight = alloc_point / total_alloc_point
    pool_rewards_per_year = annual_token_emission * pool_weight
    
    prices = tr.get_prices()
    tvl0 = prices[token0_symbol] * token0_amount
    
    return round(prices['brl']*pool_rewards_per_year/tvl0 * 100, 2)